// 現在のページを取得する。
var page = Forguncy.Page;
// 現在のページでatesakiという名前のセルを取得する。
var midcell = page.getCell("messageitsuran_messageid");
// getValueメソッドでatesakiセルの値を取得する。
var midcellValue = midcell.getValue();
var midatesaki = "メッセージユーザー?$select=宛先ユーザー&$filter=メッセージID eq " + midcellValue ;
// Forguncy.Page.getCell("TextBoxCell2").setValue(mcellValue2);

// 本サンプルコードでは、getTableDataByODataメソッドを使用して「Caterories」テーブルの全レコードを取得し、表示に適切な内容に整形したうえで「resultLabel」という名前が付いたセルに値として設定する例を示します。
Forguncy.getTableDataByOData(midatesaki,
    // 成功した場合のコールバック処理
    function (data) {//データ型 Type:List&lt;Dictionary&lt;string, object&gt;&gt;

        Forguncy.Page.getCell("TextBoxCell1").setValue(data);
    },
    // エラー時のコールバック処理
    function (e) {
        alert(e);
    });

// 現在のページでatesakiという名前のセルを取得する。
var textcell = page.getCell("TextBoxCell1");
// getValueメソッドでatesakiセルの値を取得する。
var textcellValue = textcell.getValue();

//このサンプルコードは、1回のpostリクエストを送信して、サーバーサイドでテーブルを追加・更新・削除する方法を把握するためのものです。

var postUrl = "http://localhost:37998/customapi/modifytablesdatademo/modifytablesdata";

//サーバーサイドAPIの「IDataAccess.ModifyTablesData メソッドで使用するModifyDataのデータを準備します。
var modifyData = {};

modifyData.コメントユーザー= {};
//追加
modifyData.コメントユーザー.AddRows = textcellValue ;

$.post(postUrl, JSON.stringify(modifyData), function () {
});
